import Template from "/emcJS/util/Template.js";

const img_url = "data:image/svg+xml;base64,";

/* room elements */
const TPL = new Template(`
    <style>       
        :host {
            display: flex;
            height: 50px;
            margin: 10px;
            background-color: #111111;
        }
        :host(:hover) {
            background-color: #333333;
        }
        #icon {
            display: flex;
            flex-wrap: wrap;
            width: 50px;
            color: #ffffff;
            font-size: 30px;
            justify-content: center;
            align-items: center;
            margin-right: 10px;
        }
        #detail {
            display: flex;
            flex-direction: column;
            color: #ffffff;
        }
        #name {
            flex: 1;
            display: flex;
            align-items: center;
        }
        #desc {
            flex: 1;
            display: flex;
            align-items: center;
            opacity: 0.4;
        }
        #actions {
            flex: 1;
            display: flex;
            align-items: center;
        }
        .lock-closed {
            background-position: center;
            background-size: 90% auto;
            background-repeat: no-repeat;
            background-image: url(${img_url});
        }
    </style>
    <div id="icon"></div>
    <div id="detail">
        <div id="name"></div>
        <div id="desc"></div>
    </div>
`);

class HTMLMultiplayerLobbyRoom extends HTMLElement {

    constructor() {
        super();
        this.attachShadow({mode: 'open'});
        this.shadowRoot.append(TPL.generate());
    }

    get pass() {
        return this.getAttribute('pass');
    }

    set pass(val) {
        this.setAttribute('pass', val);
    }

    get name() {
        return this.getAttribute('name');
    }

    set name(val) {
        this.setAttribute('name', val);
    }

    get desc() {
        return this.getAttribute('desc');
    }

    set desc(val) {
        this.setAttribute('desc', val);
    }

    static get observedAttributes() {
        return ['pass', 'name', 'desc'];
    }
    
    attributeChangedCallback(name, oldValue, newValue) {
        if (oldValue != newValue) {
            switch (name) {
                case 'pass':
                    if (!!newValue && newValue != "false") {
                        this.shadowRoot.getElementById("icon").classList.add("lock-closed")
                    } else {
                        this.shadowRoot.getElementById("icon").classList.remove("lock-closed")
                    }
                    break;
                case 'name':
                    this.shadowRoot.getElementById("name").innerHTML = newValue;
                    break;
                case 'desc':
                    this.shadowRoot.getElementById("desc").innerHTML = newValue;
                    break;
            }
        }
    }

}

customElements.define('ootrt-mproom', HTMLMultiplayerLobbyRoom);
